import React from "react";

const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
};

const starContainerStyle = {
  display: "flex",
  gap: "4px",
};
const textStyle = {
  lineHeight: "1",
  margin: "0",
};

function StarRating({ maxRating = 5 }) {
  return (
    <div style={containerStyle}>
      <div style={starContainerStyle}>
        {Array.from(
          { length: maxRating },
          (
            _,
            i //The Array.from() static method creates a new, shallow-copied Array instance from an iterable or array-like object.
          ) => (
            <span>S{(i = 1)}</span>
          )
        )}
      </div>
      <p style={textStyle}>10</p>
    </div>
  );
}

export default StarRating;
